import React, { useState, useEffect, Fragment } from 'react';
import { DataGrid } from '@mui/x-data-grid';
import BasicModal from './Modal';


function Envelopes() {

    const [tableData, setTableData] = useState([]);
    const [title] = useState('');
    const [budget] = useState('');
    const [id] = useState('')

    const columns = [
        { field: 'id', headerName: 'ID', editable: true },
        { field: 'title', headerName: 'Title', width: 200, editable: true },
        { field: 'budget', headerName: 'Budget', width: 150, editable: true, type: 'money' }
      ]


    const handleCommit = async (e) => {
        e.preventDefault();
        try {
          const body = {columns}
          const response = await fetch(`http://localhost:5000/api/${columns.id}`, {
            method: 'PUT',
            headers: {"Content-Type": "application/json" },
            body: JSON.stringify(body)
          } );
          console.log(response)
          setTableData(body)
        } catch (err) {
          return alert('Duplicate product not allowed')
        }
      }


    useEffect(() => {
        fetch("http://localhost:5000/api")
          .then((data) => data.json())
          .then((data) => setTableData(data))
      }, [])
      console.log('', tableData)

  return (
    <Fragment>
        <div className="header">
            <h1>Personal Budget</h1>
        </div>
    <div style={{ height: 500, width: '500px', margin: '100px 370px'}}>
     <DataGrid
       rows={tableData}
       columns={columns}
       pageSize={12}
       onCellEditCommit={handleCommit}
     />
     <BasicModal />
     <div>{JSON.stringify(tableData)}</div>
     </div>
     </Fragment>
       )
}

export default Envelopes
