import React, { Component } from 'react';
import Axios from 'axios';

export default class Envelopes extends Component {
    constructor(props) {
        super(props);

        this.state = {
            envelopes: [],
        };
    }

    componentDidMount() {
        Axios.get('http://127.0.0.1:5000/api')
            .then(function (response) {
                this.setState({
                    envelopes: response.data,
                });
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {
        const { envelopes } = this.state;
        return envelopes.length > 0 ? (
            <table striped>
                <thead>
                    <tr>
                        <td>ID</td>
                        <td>Username</td>
                        <td>Email</td>
                    </tr>
                </thead>
                <tbody>
                    {envelopes.map((envelope) => {
                        return (
                            <tr key={envelope.id}>
                                <td>{envelope.id}</td>
                                <td>{envelope.username}</td>
                                <td>{user.email}</td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        ) : (
            <h1>No data available!</h1>
        );
    }
}
