import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { TextField } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


export default function BasicModal() {
    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    const [title, setTitle] = useState('')
    const [budget, setBudget] = useState('')

const handleCreate = async () => {
    await fetch('http://localhost:5000/api', {
        method: 'POST',
        body
    })
}


    return (
      <div>
        <Button onClick={handleOpen}>Open modal</Button>
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <Typography id="modal-modal-title" variant="h6" component="h2">
              Create New Envelope
            </Typography>
            <hr/>
            <TextField id="outlined-basic" label="Title" variant="outlined" value={title} onChange={e => setTitle(e.target.value)} required/>
            <hr/>
            <TextField id="outlined-basic" label="Budget" variant="outlined" value={budget} onChange={e => setBudget(e.target.value)} required/>
            <hr/>
            <Button onClick={handleCreate}>Create</Button>
          </Box>
        </Modal>
      </div>
    );
  }
