import React, { useState, useEffect } from 'react';
import { DataGrid } from '@mui/x-data-grid';
import { Button } from '@mui/material';

function Envelopes() {

    const [tableData, setTableData] = useState([]);

    const columns = [
        { field: 'id', headerName: 'ID', editable: true },
        { field: 'title', headerName: 'Title', width: 200, editable: true },
        { field: 'budget', headerName: 'Budget', width: 150, editable: true }
      ]

    const handleCommit =  (e) => {
        const array = tableData.map(r => {
            if (r.id === e.id) {
                return {...r, [e.field]: e.value}
            } else {
                return {...r}
            }
        })
        setTableData(array)
    }


    useEffect(() => {
        fetch("http://localhost:5000/api")
          .then((data) => data.json())
          .then((data) => setTableData(data))
      }, [])
      console.log('', tableData)

  return (
    <div style={{ height: 500, width: 'auto', margin: '100px 200px'}}>

     <DataGrid
       rows={tableData}
       columns={columns}
       pageSize={12}
       onCellEditCommit={handleCommit}
     />
     <Button style={{marginRight: ''}}>Click Me</Button>
     <div>{JSON.stringify(tableData)}</div>
   </div>
  )
}

export default Envelopes
